import React, { useState } from 'react';
import { FaBug } from 'react-icons/fa';
import SearchBar from './components/SearchBar';
import NewsList from './components/NewsList';
import VisitorCounter from './components/VisitorCounter';

function App() {
  const [news, setNews] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [resetTrigger, setResetTrigger] = useState(false);

  const handleSearch = async (keyword) => {
    try {
      setIsLoading(true);
      setError(null);
      setResetTrigger(false);

      const response = await fetch(`http://localhost:5001/api/news/search?keyword=${keyword}`);
      if (!response.ok) {
        throw new Error('API 요청에 실패했습니다.');
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setNews(data.items);
    } catch (error) {
      console.error('검색 오류:', error);
      setError(error.message || '뉴스를 불러오는 중 오류가 발생했습니다.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleReset = () => {
    setNews([]);
    setError(null);
    setIsLoading(false);
    setResetTrigger(true);
  };

  return (
    <div className="container">
      <VisitorCounter />
      <div className="header">
        <div className="title-container">
          <h1 onClick={handleReset} style={{ cursor: 'pointer' }}>
            <FaBug className="bee-icon" />
            Apis의 종목 검색기
          </h1>
          <a 
            href="https://contents.premium.naver.com/apishive/apishive56" 
            target="_blank" 
            rel="noopener noreferrer" 
            className="channel-button"
          >
            주식매매의 정석 채널 바로가기
          </a>
        </div>
      </div>
      <SearchBar onSearch={handleSearch} shouldReset={resetTrigger} />
      {isLoading && <div className="loading">검색 중...</div>}
      {error && <div className="error-message">{error}</div>}
      {!isLoading && !error && <NewsList news={news} />}
    </div>
  );
}

export default App;
